/*******************************************************
 * CONTROL
 * Este nodo se encaraga del control de temperatura del
 * horno. A la entrada llegan mensajes de varios nodos.
 * Cada mensaje tiene un topic que indica su función.
 * 
 * Los tipos de entradas son temperatura, botón de
 * dashboard y botón en pin físico.
 * Cuando el programa sea más grande se repartirá
 * en varios nodos.
 ******************************************************/

var Kp = 1.2;
var Ki = 0.003;
var Kd = 0.0;
var T = 1.0; //periodo de muestreo (1 segundo)

// Si llega el valor de la temperatura actual
if (msg.topic == "Temperatura")
{
    var tem = msg.payload; //temperatura actual
    var objetivo = flow.get('objetivo') || 0; //temperatura objetivo
    var e = (objetivo - tem); //error proporcional

    var ie = flow.get('ie') || 0; //integral del error
    ie += e; //agregar error actual (evitar crecimiento rápido)
    flow.set('ie', ie); //guardar integral del error
    // tal vez se deben descartar los valores negativos

    var ePrev = flow.get('ePrev') || e; //error previo
    var de = (e - ePrev) / T; //derivada del error
    flow.set('ePrev', e); //actualizar error previo

    u = (Kp * e) + (Ki * ie) + (Kd * de); //salida
    msg.payload = String(e)+', '+String(ie)+', '+String(de)+', '+String(u);

    var out = {payload : Math.min(Math.max(u, 0), 100)};
    return [msg, out];
}

var val; //valor de temperatura

// si llega un mensaje con la temperatura objetivo
if (msg.topic == 'Objetivo') {
    val = msg.payload; //leer valor
    //guardar en variable del flujo
    flow.set('objetivo', val);
}
// si llega otro mensaje, re-enviar valor guardado
else {
    // leer valor o 0 si no se encuentra
    val = flow.get('objetivo') || 0;
    msg.payload = val;
    msg.topic = 'Objetivo';
    return msg;
}
